import React, { useState, Component } from "react";
import AddIcon from "@material-ui/icons/Add";
import Fab from "@material-ui/core/Fab";
import Zoom from "@material-ui/core/Zoom";
import Picker from "../drive/Picker";
// import FormControlLabel from "@material-ui/core/FormControlLabel";
// import Checkbox from "@material-ui/core/Checkbox";
// import FormControl from "@material-ui/core/FormControl";
// import TextField from "@material-ui/core/TextField";
// import FormGroup from "@material-ui/core/FormGroup";
// import DatePickers from "./Date";
import GooglePicker from "react-google-picker"

const CLIENT_ID = '40688782025-94ecbiq95be33gcjg9pnnd7s29nnhblp.apps.googleusercontent.com';
const DEVELOPER_KEY = 'AIzaSyC4YNy_d-jOAhb47tfynt8UZjWLq_5iPgE';
const SCOPE = ['https://www.googleapis.com/auth/drive'];

function CreateCampaign(props) {
  // Create campaign handling
  const [campaign, setCampaign] = useState({
    name: "",
    summary: "",
    channels: "",
    due: "",
    folder: ""
  });

  const [isExpanded, setExpanded] = useState(false);

  function handleChange(event) {
    const { name, value } = event.target;
    setCampaign((prevCampaign) => {
      return {
        ...prevCampaign,
        [name]: value
      };
    });
  }

  function submitCampaign(event) {
    props.onAdd(campaign);
    event.preventDefault();
    setCampaign({
      name: "",
      summary: "",
      channels: "",
      due: "",
      folder: ""
    });
  }

  function expand() {
    setExpanded(true);
  }

  return (
    <div>
      <form className="create-campaign">
        {isExpanded ? (
          <div className="campaign-div">
          <input
            type="text"
            onChange={handleChange}
            name="name"
            placeholder="Campaign Name"
            value={campaign.name}
          />
          </div>
        ) : null}

        {isExpanded ? (
          <div className="campaign-div">
            <label for="channels" className="campaign-heading">Choose a campaign type:</label>
            <select name="channels" id="channels" onChange={handleChange}>
              <option value="Email">Email</option>
              <option value="Social">Social</option>
              <option value="Direct">Direct</option>
              <option value="Tradeshow">Tradeshow</option>
              <option value="Other (explain in summary)">Other</option>
            </select>
          </div>
        ) : null}
        <div className="campaign-div">
        <textarea
          onClick={expand}
          onChange={handleChange}
          name="summary"
          placeholder="Write your campaign summary"
          rows={isExpanded ? 3 : 1}
          value={campaign.summary}
        />
        </div>
        {isExpanded ? (
          <div className="campaign-div">
            <p className="campaign-heading">Campaign Kickoff</p>
            <input
              label="Project Due"
              type="date"
              onChange={handleChange}
              name="due"
              value={campaign.due}
            />
          </div>
        ) : null}
        {isExpanded ? (
          <div className="campaign-div">
          <div className="container">
      <GooglePicker clientId={CLIENT_ID}
              developerKey={DEVELOPER_KEY}
              scope={SCOPE}
              onChange={data => console.log('on change:', data)}
              onAuthFailed={data => console.log('on auth failed:', data)}
              multiselect={true}
              navHidden={true}
              authImmediate={false}
              viewId={'FOLDERS'}
              createPicker={ (google, oauthToken) => {
                const googleViewId = google.picker.ViewId.FOLDERS;
                const docsView = new google.picker.DocsView(googleViewId)
                    .setIncludeFolders(true)
                    .setMimeTypes('application/vnd.google-apps.folder')
                    .setSelectFolderEnabled(true);

                const picker = new window.google.picker.PickerBuilder()
                    .addView(docsView)
                    .setOAuthToken(oauthToken)
                    .setDeveloperKey(DEVELOPER_KEY)
                    .setCallback((data)=>{
                        var url = 'nothing';
                         if (data[google.picker.Response.ACTION] == google.picker.Action.PICKED) {
                        var doc = data[google.picker.Response.DOCUMENTS][0];
                        url = doc[google.picker.Document.URL];
                        }
                        var folderUrl = url;
                        console.log(folderUrl);
                    });

                picker.build().setVisible(true);
            }}
        >
            <span className="picker-button">Select a campaign folder</span>

            <div className="google"></div>
        </GooglePicker>
        </div>
          </div>
        ) : null}
        <Zoom in={isExpanded}>
          <Fab onClick={submitCampaign}>
            <AddIcon />
          </Fab>
        </Zoom>
      </form>
    </div>
  );
}

export default CreateCampaign;
